mapper.insert:
insert 
    into tb_add_out
    (
     add_no
   , mem_no
   , add_name
    )
  values
   (
    seq_aono.nextval
  , #{memNo}
  , #{addName}
   )

mapper.select:
select 
    count(*)
from tb_address
where mem_no = #{memNo}
  and add_no = #{addNo}

mapper.insert:
INSERT 
    INTO TB_ADDRESS
    (
	     ADD_PER_NO
	   , MEM_NO
	   , ADD_NO
	   , ADD_NAME
	   , ADD_DEPT
	   , ADD_JOB
	   , ADD_PHONE
	   , ADD_EMAIL
	   , ADD_MEMO
   	)
   VALUES
   (
	    SEQ_ADNO.NEXTVAL
	  , #{memNo}
	  , #{addNo}
	  , #{addName}
	  , #{addDept}
	  , #{addJob}
	  , #{addPhone}
	  , #{addEmail}
	  , #{addMemo}
  	)

mapper.delete:
delete 
    from tb_address
where mem_no = #{memNo}
  and add_no = #{addNo}

delete
    from tb_add_out
where mem_no = #{memNo}
  and add_no = #{addNo}

mapper.update:
update
	 tb_address
   set add_no = #{addNo}
     , add_name = #{addName}
     , add_dept = #{addDept}
     , add_job = #{addJob}
     , add_phone = #{addPhone}
     , add_email = #{addEmail}
     , add_memo = #{addMemo}
     , add_enroll_date = sysdate
   where add_per_no = #{addPerNo}
