mapper.insert:
insert
  into tb_book
  (
  	book_no
  , mem_no
  , bk_title
  , bk_content
  )
  values
  (
    seq_bkno.nextval
  , #{memNo}
  , #{bkTitle}
  , #{bkContent}
  )

mapper.update:
update
       tb_book
   set bk_title = #{bkTitle}
     , bk_content = #{bkContent}
 where book_no = #{bookNo}
