mapper.update:
update tb_mail_status


			set mail_important = 'Y'


			set mail_important = 'N'


	, mail_change_date = sysdate
where mail_no = #{mailNo}


			and send_mail = #{sendMail}


		  and receive_mail = #{receiveMail}


  and mail_folder = #{mailFolder}

mapper.insert:
insert 
    into tb_mail_status
    (
      mail_no
    , send_mail
    , receive_mail
    , mail_folder
    )
 values
    (
      seq_mano.currval
    , #{sendMail}
    , #{receiveMail}
    , #{mailFolder}
     )

mapper.update:
update 
    tb_mail_status
    set mail_delete = 'Y'
	  , mail_change_date = sysdate
where mail_no = #{mailNo}
  and receive_mail = #{receiveMail}
  and send_mail = #{sendMail}

update
    tb_mail_status
    set mail_read = 'Y'
      , mail_change_date = sysdate
where mail_no = #{mailNo}


	 and mail_folder = 1
  	 and send_mail = #{sendMail}


	 and mail_folder in (2,3)
  	 and receive_mail = #{receiveMail}
