mapper.insert:
insert
    into tb_mail
    (
      mail_no
    , mem_no
    , receiver_mem
    , cc_mem
    , mail_title
    , mail_content
    , send_date
    )
 values
    (
      seq_mano.nextval
    , #{memNo}
    , #{receiverMem}
    , #{ccMem}
    , #{mailTitle}
    , #{mailContent}
    , sysdate
    )

insert 
    into tb_mail_status
    (
      mail_no
    , send_mail
    , receive_mail
    , mail_folder
    )
 values
    (
      seq_mano.currval
    , #{sendMail}
    , #{receiveMail}
    , #{mailFolder}
     )
