mapper.insert:
insert
  into tb_reply
  (
  	reply_no
  , reply_type
  , re_board_no
  , reply_depth
  , reply_parent
  , reply_content
  , reply_writer
  , reply_job
  , reply_date
  , reply_wno
  )
  values
  (
  	seq_reno.nextval
  , 4
  , #{reBoardNo}
  , #{replyDepth}
  , #{replyParent}
  , #{replyContent}
  , #{replyWriter}
  , #{replyJob}
  , sysdate
  , #{replyWno}
  )

mapper.update:
update tb_board
   set board_status = 'N'
 where board_no = #{boardNo}

update tb_board
   set board_title = #{boardTitle}
     , board_content = #{boardContent}
     , board_mo_date = sysdate
 where board_no = #{boardNo}

update tb_attachment
   set at_status = 'N'
 where at_no = #{atNo}
