mapper.insert:
insert
  into tb_class
  (
    class_no
  , class_title
  , class_content
  , class_objective
  , class_subject
  , class_student
  , class_start_date
  , class_end_date
  , mem_no
  , class_approval
  , class_status
  , class_fee
  , class_signup_date
  )
  values
  (
    seq_clno.nextval 
  ,	#{classTitle}
  ,	#{classContent}
  ,	#{classObjective}
  ,	#{classSubject}
  ,	#{classStudent}
  ,	#{classStartDate}
  ,	#{classEndDate}
  , #{memNo}
  , default
  , default
  ,	#{classFee}
  , sysdate
  )

mapper.update:
update
       tb_class
   set class_title = #{classTitle}
     , mem_no = #{memNo}
     , class_start_date = #{classStartDate}
     , class_end_date = #{classEndDate}
     , class_fee = #{classFee}
     , class_signup_date = sysdate
     , class_content = #{classContent}
 where class_no = #{classNo}

update
       tb_class
   set class_approval = #{classApproval}
     , class_status = 'Y'
 where class_no = #{classNo}
